INSERT INTO `esm_operations` VALUES (960,'new_order',472,2,'\r\n<div class=\"w-box\">\r\n<div class=\"w-box-header\">\r\n<h3 style=\"display:inline-block\">List of Order</h3>\r\n\r\n</div>\r\n<div class=\"w-box-content cnt_a\">\r\n<%=render :inline=>this.init%>\r\n\r\n\r\n<style>\r\n    .label{\r\n    }\r\n    select{\r\n        width:auto;\r\n    }\r\n    \r\n    .k-icon { float:left;}\r\n    .k-link { float:left;}\r\n    .k-reset { float:left;}\r\n\r\n</style>\r\n\r\n<div class=\"container_12\" style=\"background:#fff;border:0px solid #ccc; width:auto;\">\r\n<!--\r\n<div id=\"item-type\" style=\"border:0px solid #aaa;height:100px\" class=\"grid_10 alert\" > \r\n\r\n<h3>1. Select order type</h3><br/>\r\n    <p>\r\n    <%=radio_button_tag \'item_type\',\'0\', true , :id=>\'item-0\',:onclick=>\'change_type(0)\'%> Patient\'s personal prescription\r\n    </p>\r\n    <p>\r\n    <%#radio_button_tag \'item_type\',\'1\', false , :id=>\'item-1\',:onclick=>\'change_type(1)\'%> <%#Non-specific patient e.g. bulk order %>\r\n    </p>\r\n</div>\r\n-->\r\n<%= hidden_field_tag \'item_type\',\'0\' %>\r\n\r\n<div class=\"row-fluid\">\r\n<div id=\"patient-panel\" style=\"border:0px solid #aaa;height:auto\"  class=\"well span4\" > \r\n<div class=\"breadCrumb\">\r\n<h4>Patient Information</h4></div>\r\n    <p>\r\n    <span class=\"label\"> Patient\'s name :</span><br />\r\n    <%=text_field_tag \'patient_name\',\'\',:onkeyup=>\'search_patient()\' %>\r\n  </p><p>\r\n    <span class=\"label\"> Patient\'s HN :</span><br />\r\n    <%=text_field_tag \'patient_hn\',\'\',:onkeyup=>\'search_patient()\' %>\r\n    </p>\r\n    \r\n    <%=button_tag \'New Patient\',:onclick=>\'new_patient()\',:class=>\"btn\" %>\r\n    <%=button_tag \'Reset\',:onclick=>\'$(\"#patient_name\").val(\"\");$(\"#patient_hn\").val(\"\");\',:class=>\"btn\" %>\r\n    <%=button_tag \'Search\',:onclick=>\'search_patient()\',:class=>\"btn\" %>\r\n    <br/><br/>\r\n  \r\n</div>\r\n\r\n  \r\n\r\n<div id=\"new-patient-panel\" cla