r\n        }\r\n        \r\n        \r\n        \r\n    }\r\n    function search_patient(){\r\n        \r\n        params = \'name=\'+$(\'#patient_name\').val();\r\n        params = params+\'&hn=\'+$(\'#patient_hn\').val();\r\n        params = params+\'&cus_id=<%=@current_customer.id%>\'\r\n        params = params+\'&cli_id=<%=@current_clinician.id%>\'\r\n        \r\n        \r\n        $.ajax({url: \'patient_list?\'+params, success: function(data){\r\n            \r\n            $(\'#patient-list\').html(data);    \r\n            \r\n        }});\r\n        \r\n        $(\'#formula-list\').hide();\r\n        $(\'#patient-list\').show();\r\n\r\n        \r\n    }\r\n    function new_patient(){\r\n        \r\n        $(\'#new-name\').val($(\'#patient_name\').val());\r\n        $(\'#new-hn\').val($(\'#patient_hn\').val());\r\n        \r\n        $(\'#new-patient-panel\').show();    \r\n        $(\'#formula-list\').hide();\r\n        $(\'#patient-list\').hide();\r\n\r\n        \r\n    }\r\n    function cancel_new_patient(){\r\n        \r\n       $(\'#new-patient-panel\').hide(); \r\n        $(\'#patient-panel\').show();  \r\n        $(\'#formula-list\').hide();\r\n        preventDefault();\r\n        return false;\r\n        \r\n    }\r\n    \r\n    function select_patient( patient_id ){\r\n        \r\n        //alert(\'select patient for \'+patient_id)\r\n        \r\n        params = \'\'\r\n        \r\n        params = params+\'&cus_id=<%=@current_customer.id%>\'\r\n        params = params+\'&cli_id=<%=@current_clinician.id%>\'\r\n        params = params+\'&p_id=\'+patient_id ;\r\n        \r\n        \r\n        $.ajax({url: \'formula_list?\'+params, success: function(data){\r\n            \r\n            $(\'#formula-list\').html(data);    \r\n            \r\n        }});\r\n        \r\n        $(\'#patient-list\').hide();\r\n         \r\n        $(\'#formula-list\').show();\r\n        // show formula with history\r\n    }\r\n    \r\n    function select_formula(patient_id, formula_id ) {\r\n        \r\n       // alert(\'select formula for \'+formula_id)\r\n        params = \'\'\r\n        \r\n        if(formula_id==\'\')formula_id=\'-\'\r\n        params = params+\'&pat_id=\'+patient_id\r\n        params = params+\'&for_id=\'+formula_id\r\n        params = params+\'&cus_id=<%=@current_customer.id%>\'\r\n        params = params+\'&cli_id=<%=@current_clinician.id%>\'\r\n        \r\n        window.location = \'new_order_action?\'+params\r\n        \r\n    }\r\n    \r\n    \r\n    \r\n    \r\n    \r\n</script>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n',NULL,NULL,NULL,'2014-01-14 12:25:52','2014-03-31 01:17:23','new_order',''),(961,'report_partial',401,3,'<%\r\n    \r\n    ehr = @context.get_projects_by_abstract(\'ehr.ehr\')[0]\r\n    model = ehr.load_model\r\n    patient_model = model[:patient]\r\n    module_model = model[:patient_module]\r\n    patients = [] #patient_model.all :select=>[:date]\r\n    mname = \"\\\\#{@current_project.name}#appointment\"\r\n    patients =  module_model.all(:module_name=>mname)\r\n    \r\n    m = params[:q].split(\"-\")\r\n    mo = m[0].split.join(\"_\").downcase\r\n    month = m[1]\r\n    \r\n    mname = \"\\\\#{@current_project.name}#\\\\#{mo}\"\r\n       \r\n       \r\n     list = module_model.collection.find(:module_name=>mname).to_a\r\n    \r\n    \r\n%>\r\n<%=mname %>\r\n<%=month %>\r\n<%#list.inspect %>',NULL,NULL,NULL,'2014-01-14 15:32:37','2014-01-14 15:32:37','report_partial',''),(962,'list',473,3,'<%\r\n  selected_module = \'crrt_day\'\r\n  selected_module = params[:smodule] if params[:smodule]\r\n  selected_module = \"rrt\\#\\\\#{selected_module}\"\r\n\r\n%>\r\n<%= link_to \'RRT report\', \"index?smodule=rrt_report\"%> |\r\n<%= link_to \'CRRT day\', \"index?smodule=crrt_day\"%> |\r\n<%= link_to \'Sled day\', \"index?smodule=sled_day\"%> |\r\n<%= link_to \'Outcome\', \"index?smodule=outcome\"%>\r\n\r\n<%\r\n   model = get_model(this.table_name)\r\n   records = []\r\n%>\r\n<%# params[:search] %>\